import React, { Component } from "react";
import "./counter.css";
import logo from "./cine.png";
import search from "./searchicon.jpg";
import Autosuggest from "react-autosuggest";
import { Navbar, Nav } from "react-bootstrap";
class Counter extends Component {
  render() {
    let videoid = "vi2308751129";
    let link = `https://www.imdb.com/videoembed/${videoid}`;
    const data = [
      {
        id: 100,
        name: "CSCI-4448",
        interested_count: 1,
        interested_peers: ["peer1", "peer2", "peer3"]
      },

      {
        id: 200,
        name: "name2",
        interested_count: 2,
        interested_peers: ["peer4", "peer5", "peer6"]
      },
      {
        id: 101,
        name: "name1",
        interested_count: 1,
        interested_peers: ["peer1", "peer2", "peer3"]
      },
      {
        id: 102,
        name: "name1",
        interested_count: 1,
        interested_peers: ["peer1", "peer2", "peer3"]
      },
      {
        id: 103,
        name: "name1",
        interested_count: 1,
        interested_peers: ["peer1", "peer2", "peer3"]
      },
      {
        id: 104,
        name: "name1",
        interested_count: 1,
        interested_peers: ["peer1", "peer2", "peer3"]
      }
    ];
    state = {
      courses: ["CS", "MS", "FRCS"],
      skills: ["C", "C++"],
      value: "",
      suggestions: [],
      cacheAPISugesstions: [],
      isOpen: false,
      data: data,
      filterResults: data
    };

    SUGGEST_URL = "https://api-suggest-dot-studybuddy-5828.appspot.com/suggest";

    function componentWillMount() {
      this.onSuggestionsFetchRequested = debounce(
        500,
        this.onSuggestionsFetchRequested
      );
    }

    renderSuggestion = suggestion => {
      return (
        // <ul className="ui-autocomplete">

        <div>
          <span>{suggestion.post}</span>
          <span>{suggestion.course}</span>
        </div>
      );
    };

    onChange = (event, { newValue }) => {
      this.setState({
        value: newValue
      });
    };

    function componentDidMount() {
      axios.get(this.SUGGEST_URL, {}).then(res => {
        this.setState({ cacheAPISugesstions: res.data });
      });
    }

    onSuggestionsFetchRequested = ({ value }) => {
      this.setState({
        suggestions: this.getSuggestions(this.state.cacheAPISugesstions, value)
      });
    };

    // Autosuggest will call this function every time you need to clear suggestions.
    onSuggestionsClearRequested = () => {
      this.setState({
        suggestions: []
      });
    };

    onSuggestionSelected = (
      event,
      { suggestion, suggestionValue, suggestionIndex, sectionIndex, method }
    ) => {
      var filterRes = this.state.data;
      filterRes = filterRes.filter(item => item.name == suggestionValue);

      if (filterRes != 0) {
        this.setState({
          filterResults: filterRes
        });
      } else {
        this.setState({
          filterResults: this.state.data
        });
      }
    };
    getSuggestions = (allPosts, searchValue) => {
      const inputValue = searchValue.trim().toLowerCase();
      const inputLength = inputValue.length;

      return inputLength === 0
        ? []
        : allPosts.filter(s => s.course.toLowerCase().includes(inputValue));
    };

    toggleModal = () => {
      this.setState({
        isOpen: !this.state.isOpen
      });
    };

    const value = this.state.value;
    const suggestions = this.state.suggestions;

    // Autosuggest will pass through all these props to the input.
    const autoSuggestInputProps = {
      placeholder: "Search..",
      value,
      onChange: this.onChange
    };

    const renderInputComponent = inputProps => (
      <div className="inputContainer">
        <img
          className="icon"
          src="https://img.icons8.com/ios-filled/50/000000/search.png"
        />
        <input {...inputProps} />
      </div>
    );

    function myFunction() {
      console.log("Hello!");
    }
    function sayHello() {
      alert("Hello 123!");
    }
    return (
      <div id="page">
        <div class="header">
          <span class="logoheader">
            <img
              alt=""
              src={logo}
              width="50"
              height="50"
              backgroundColor="transparent"
            />
            <text>CINEPHILE</text>
          </span>
          <Nav class="collapse navbar-collapse justify-content-center" padded>
            <Autosuggest
              suggestions={suggestions}
              onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
              onSuggestionsClearRequested={this.onSuggestionsClearRequested}
              getSuggestionValue={suggestion => suggestion.course}
              renderSuggestion={this.renderSuggestion}
              onSuggestionSelected={this.onSuggestionSelected}
              inputProps={autoSuggestInputProps}
              renderInputComponent={renderInputComponent}
            />
          </Nav>
          <div class="searchbar">
            <input
              type="text"
              id="myInput"
              onKeyDownCapture={myFunction}
              placeholder=" Enter movie Name here"
              onFocus={e => (e.target.placeholder = "")}
              onBlur={e => (e.target.placeholder = " Enter movie Name here")}
            ></input>
            <button class="gosearch" onClick={sayHello}>
              <img alt="" src={search} width="30" height="28" />
            </button>
          </div>{" "}
          {/* search bar div ends here */}
        </div>
        {/* header div ends here */}
        <div id="all_details">
          <div id="moviereview">
            <div id="trailer" style={{ backgroundColor: "#cde" }}>
              <iframe
                src={link}
                width="550"
                height="260"
                allowfullscreen="true"
                mozallowfullscreen="true"
                webkitallowfullscreen="true"
                frameborder="no"
                scrolling="no"
              ></iframe>
            </div>
            <div id="tomatometer" style={{ backgroundColor: "#abc" }}>
              Tomatometer here
            </div>
            <div id="cast" style={{ backgroundColor: "#cdc" }}>
              Cast details
            </div>
          </div>
          <div id="twitterfeed">
            <div id="news">NEWS</div>
            <div id="feed"> Twitter Feed</div>
          </div>

          <div id="screen_location">
            <div id="location">LOCATION</div>
            <div id="maps"> Maps</div>
          </div>
        </div>
      </div>
    );
  }
}

export default Counter;
